import os
import subprocess  # Để mở ứng dụng Free Fire
from kivy.app import App
from kivy.uix.boxlayout import BoxLayout
from kivy.uix.togglebutton import ToggleButton
from kivy.uix.button import Button
from kivy.uix.label import Label

class MenuLayout(BoxLayout):
    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.orientation = 'vertical'
        self.padding = 10
        self.spacing = 10

        # Toggle buttons
        self.add_widget(self.create_toggle("AIMBOT HEX :", self.on_toggle, "hex_jump_aimbot.hex"))
        self.add_widget(self.create_toggle("AIMLOCK HEAD HEX :", self.on_toggle, "hex_aimlock_head.hex"))
        self.add_widget(self.create_toggle("LIB2CPP REGEDIT ANTIBAN HEX :", self.on_toggle, "hex_lib2cpp_max.hex"))
        self.add_widget(self.create_toggle("AIMCHEAT VIP HEX :", self.on_toggle, "hex_aimcheat_vip.hex"))
        self.add_widget(self.create_toggle("SPEED HEADSHOT HEX :", self.on_toggle, "hex_speed_headshot.hex"))
        self.add_widget(self.create_toggle("WHITE444 MODE HEX :", self.on_toggle, "hex_aimlock_mode.hex"))

        # Bypass, Free Fire and Clear All buttons
        self.add_widget(Button(text="BYPASS", size_hint=(1, None), height=50, background_color=(0.6, 0, 0.6, 1), on_press=self.bypass))
        self.add_widget(Button(text="MỞ FREE FIRE", size_hint=(1, None), height=50, background_color=(0.6, 0, 0.6, 1), on_press=self.open_free_fire))
        self.add_widget(Button(text="MỞ FF MAX", size_hint=(1, None), height=50, background_color=(0.6, 0, 0.6, 1)))
        self.add_widget(Button(text="XÓA TẤT CẢ", size_hint=(1, None), height=50, background_color=(0.6, 0, 0.6, 1), on_press=self.clear_all_files))

        # Store directories for file management
        self.target_dirs = {
            "hex_jump_aimbot.hex": r'/storage/emulated/0/Android/obb/com.dts.freefireth',
            "hex_aimlock_head.hex": r'/storage/emulated/0/Android/obb/com.dts.freefireth',
            "hex_lib2cpp_max.hex": r'/storage/emulated/0/Android/obb/com.dts.freefireth',
            "hex_aimcheat_vip.hex": r'/storage/emulated/0/Android/obb/com.dts.freefireth',
            "hex_speed_headshot.hex": r'/storage/emulated/0/Android/obb/com.dts.freefireth',
            "hex_aimlock_mode.hex": r'/storage/emulated/0/Android/obb/com.dts.freefireth',
        }

    def create_toggle(self, text, callback=None, filename=None):
        layout = BoxLayout(size_hint=(1, None), height=50)
        label = Label(text=text, size_hint=(0.7, 1))
        toggle = ToggleButton(size_hint=(0.3, 1))
        if callback:
            toggle.bind(on_press=lambda instance: callback(instance, filename))
        layout.add_widget(label)
        layout.add_widget(toggle)
        return layout

    def on_toggle(self, instance, filename):
        target_dir = self.target_dirs.get(filename, r'/storage/emulated/0/Default/Directory')
        target_file = os.path.join(target_dir, filename)

        if instance.state == 'down':
            if not os.path.exists(target_dir):
                os.makedirs(target_dir)
            self.write_hex_content(filename, target_file)
        else:
            if os.path.exists(target_file):
                os.remove(target_file)

    def write_hex_content(self, filename, target_file):
        content = self.get_content_for_file(filename)
        with open(target_file, 'w') as f:
            f.write(content)

    def get_content_for_file(self, filename):
        # Define specific content for each file
        content_map = {
            "hex_jump_aimbot.hex": "",
            "hex_aimlock_head.hex": "",
            "hex_lib2cpp_max.hex": "",
            "hex_aimcheat_vip.hex": "",
            "hex_speed_headshot.hex": "",
            "hex_aimlock_mode.hex": "",
            "hex_bypass.hex": "",
        }
        return content_map.get(filename, "Default content")

    def clear_all_files(self, instance):
        # Delete all files created by the toggle buttons
        for filename in self.target_dirs.keys():
            target_dir = self.target_dirs[filename]
            target_file = os.path.join(target_dir, filename)
            if os.path.exists(target_file):
                os.remove(target_file)

    def bypass(self, instance):
        filename = "hex_bypass.hex"
        target_dir = r'/storage/emulated/0/Android/obb/com.dts.freefireth'
        target_file = os.path.join(target_dir, filename)

        if not os.path.exists(target_dir):
            os.makedirs(target_dir)
        if not os.path.exists(target_file):
            self.write_hex_content(filename, target_file)

        # Toggle the button color
        instance.background_color = (0, 1, 0, 1) if instance.background_color == (0.6, 0, 0.6, 1) else (0.6, 0, 0.6, 1)

    def open_free_fire(self, instance):
        # Mở ứng dụng Free Fire trên thiết bị Android
        package_name = "com.dts.freefireth"
        try:
            # Sử dụng adb để mở ứng dụng
            subprocess.Popen(["adb", "shell", "monkey", "-p", package_name, "-c", "android.intent.category.LAUNCHER", "1"])
        except Exception as e:
            print(f"Error opening Free Fire: {e}")

class GameSettingsApp(App):
    def build(self):
        return MenuLayout()

if __name__ == '__main__':
    GameSettingsApp().run()
